mapper.insert:
INSERT INTO trip
SET title = #{title}
    , manager_id = #{managerId}
    , start_at = #{startAt}
    , end_at = #{endAt}

mapper.update:
UPDATE trip
SET title = #{title}
    , start_at = #{startAt}
    , end_at = #{endAt}
WHERE trip_id = #{tripId}

mapper.select:
SELECT sm.schedule_memo_id FROM sche_memo sm JOIN memo m ON m.memo_id = sm.schedule_memo_id AND sm.trip_id = #{tripId} JOIN trip_user tu ON tu.trip_user_id = m.trip_user_id WHERE tu.user_id IN (
#{userId}
)

mapper.delete:
DELETE FROM memo
WHERE memo_id IN (

    #{id}

)

DELETE FROM trip_user
WHERE user_id IN (

    #{id}

)
AND trip_id = #{tripId}

DELETE FROM sche_memo
WHERE schedule_memo_id IN (

    #{id}

)

DELETE FROM trip_location
WHERE location_id IN (

    #{id}

)
AND trip_id = #{tripId}

mapper.insert:
INSERT INTO schedule
