mapper.update:

UPDATE sche_memo
SET seq = seq - 1
WHERE trip_id = #{tripId}
AND seq > #{seq}

choose.when:
UPDATE schedule
SET distance = #{distance}
, duration = #{duration}
, pathtype = #{pathType}
WHERE schedule_id = #{nextScheduleId}

mapper.select:
SELECT strf_id, lat, lng FROM stay_tour_restaur_fest
strf_id = #{prevStrfId}
OR
strf_id = #{nextStrfId}

mapper.update:
UPDATE sche_memo
SET seq = #{destSeq}
WHERE schedule_memo_id = #{scheduleMemoId}

choose.when:
UPDATE sche_memo
SET seq = seq + 1
WHERE trip_id = #{tripId}
    AND seq BETWEEN #{destSeq} AND #{originSeq}

choose.otherwise:
UPDATE sche_memo
SET seq = seq - 1
WHERE trip_id = #{tripId}
    AND seq BETWEEN #{originSeq} AND #{destSeq}

mapper.update:
UPDATE sche_memo
SET day = #{destDay}
WHERE schedule_memo_id = #{scheduleMemoId}
