mapper.insert:
INSERT INTO trip_review
   SET trip_review_id = #{tripReviewId}
     , trip_id = #{tripId}
     , user_id = #{userId}
     , title = #{title}
     , content = #{content}

mapper.update:
UPDATE trip_review


        title = #{title}


        , content = #{content}


WHERE trip_review_id = #{tripReviewId}

mapper.insert:
INSERT INTO trip_like
   SET user_id = #{userId}
     , trip_review_id = #{tripReviewId}

mapper.delete:
DELETE FROM trip_like
 WHERE user_id = #{userId}
   AND trip_review_id = #{tripReviewId}

mapper.select:
SELECT COUNT(user_id)
  FROM trip_like
 WHERE trip_review_id = #{tripReviewId}

mapper.insert:
INSERT INTO scrap
   SET trip_review_id = #{tripReviewId}
     , trip_id = #{tripId}

mapper.select:
SELECT COUNT(*)
  FROM trip_review
 WHERE trip_review_id = #{tripReviewId}
   AND trip_id = #{tripId}

mapper.insert:
INSERT INTO trip (title, manager_id, start_at, end_at)
SELECT title, #{userId}, #{newStartAt}, #{newEndAt}
  FROM trip
 WHERE trip_id = #{copyTripId}

mapper.select:
SELECT schedule_memo_id
  FROM sche_memo
 WHERE trip_id = #{tripId}

SELECT location_id
  FROM trip_location
 WHERE trip_id = #{tripId}

SELECT COUNT(scrap_id)
  FROM scrap
 WHERE trip_review_id = #{tripReviewId}
